import React, { useState, useEffect } from 'react';
import './HomePage.css'; // Vamos criar este arquivo de estilo a seguir
import { Link } from 'react-router-dom';

function HomePage() {
  // 1. Criamos um estado para guardar a lista de filmes. Começa como uma lista vazia.
  const [filmes, setFilmes] = useState([]);

  // 2. useEffect é executado uma vez, quando o componente é montado na tela.
  useEffect(() => {
    // Função para buscar os filmes da nossa API
    const buscarFilmes = async () => {
      try {
        const response = await fetch('http://localhost:8080/filmes');
        const data = await response.json(); // Converte a resposta para JSON
        setFilmes(data); // 3. Atualiza o estado com a lista de filmes recebida
      } catch (error) {
        console.error("Erro ao buscar filmes:", error);
      }
    };

    buscarFilmes();
  }, []); // O array vazio [] garante que isso rode apenas uma vez.

  return (
    <div className="homepage-container">
      <header className="homepage-header">
        <h1>CineRecomenda</h1>
        <p>Sua lista de filmes definitiva.</p>
      </header>

      <main className="movie-grid">
        {/* 4. Usamos .map() para percorrer a lista de filmes e criar um "card" para cada um */}
        {filmes.map(filme => (
          <Link to={`/filmes/${filme.idFilme}`} key={filme.idFilme} className="movie-card-link">
    <div className="movie-card">
      <h2>{filme.nome}</h2>
      <p><strong>Lançamento:</strong> {new Date(filme.anoLanc).toLocaleDateString()}</p>
      <p><strong>Duração:</strong> {filme.duracao}</p>
    </div>
  </Link>
))}
      </main>
    </div>
  );
}

export default HomePage;
