import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
import HomePage from './HomePage';
import './App.css';

function App() {
  // 1. Criamos um estado para saber se o usuário está logado ou não
  //    Para testes, você pode mudar para 'true' para ir direto para a home
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // 2. Criamos uma função que será chamada pelo componente Login
  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Passamos a função para o componente Login */}
          <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />

          {/* 3. A rota principal agora é "protegida" */}
          <Route
            path="/"
            element={isLoggedIn ? <HomePage /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
