import React, { useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import './FilmeDetalhe.css';

function FilmeDetalhe() {
    const [filme, setFilme] = useState(null);
    const [avaliacoes, setAvaliacoes] = useState([]);
    const { id } = useParams();

    // --- NOVOS ESTADOS PARA O FORMULÁRIO DE AVALIAÇÃO ---
    const [nota, setNota] = useState('');
    const [comentario, setComentario] = useState('');

    // --- LÓGICA PARA BUSCAR DADOS (Refatorada) ---
    // Colocamos a busca de avaliações em sua própria função para podermos chamá-la novamente
    const buscarAvaliacoes = useCallback(() => {
        fetch(`http://localhost:8080/avaliacoes/filme/${id}`)
            .then(response => response.json())
            .then(data => setAvaliacoes(data))
            .catch(error => console.error("Erro ao buscar avaliações:", error));
    }, [id]);

    useEffect(() => {
        // Busca os detalhes do filme
        fetch(`http://localhost:8080/filmes/${id}`)
            .then(response => response.json())
            .then(data => setFilme(data))
            .catch(error => console.error("Erro ao buscar detalhes do filme:", error));

        // Busca as avaliações quando o componente carrega
        buscarAvaliacoes();
    }, [id, buscarAvaliacoes]); // Adicionamos buscarAvaliacoes à lista de dependências

    // --- NOVA FUNÇÃO PARA ENVIAR A AVALIAÇÃO ---
    const handleAvaliacaoSubmit = async (event) => {
        event.preventDefault();

        // IMPORTANTE: Em um app real, o idUsuario viria do estado de login.
        // Por agora, vamos usar o ID 1 como exemplo.
        const idUsuario = 1;

        const novaAvaliacao = {
            nota: parseInt(nota), // Converte a nota para número
            comentario: comentario
        };

        try {
            const response = await fetch(`http://localhost:8080/avaliacoes?idUsuario=${idUsuario}&idFilme=${id}`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(novaAvaliacao)
            });

            if (response.ok) {
                alert('Avaliação enviada com sucesso!');
                setNota(''); // Limpa o campo de nota
                setComentario(''); // Limpa o campo de comentário
                buscarAvaliacoes(); // Re-busca a lista de avaliações para atualizar a tela!
            } else {
                alert('Falha ao enviar avaliação.');
            }
        } catch (error) {
            console.error("Erro ao enviar avaliação:", error);
            alert('Erro ao conectar com o servidor.');
        }
    };

    if (!filme) {
        return <div>Carregando...</div>;
    }

    return (
        <div className="detalhe-container">
            {/* Seção de detalhes do filme (continua a mesma) */}
            <h1>{filme.nome}</h1>
            <div className="detalhe-content">
                {/* ... outros detalhes ... */}
            </div>
            <div className="generos-container">
                {/* ... seção de generos ... */}
            </div>

            {/* --- SEÇÃO DE AVALIAÇÕES ATUALIZADA --- */}
            <div className="avaliacoes-section">
                <h2>Avaliações</h2>
                
                {/* FORMULÁRIO PARA NOVA AVALIAÇÃO */}
                <form onSubmit={handleAvaliacaoSubmit} className="avaliacao-form">
                    <h3>Deixe sua avaliação</h3>
                    <div className="form-group">
                        <label htmlFor="nota">Nota (0 a 10):</label>
                        <input
                            type="number"
                            id="nota"
                            min="0"
                            max="10"
                            value={nota}
                            onChange={(e) => setNota(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="comentario">Comentário:</label>
                        <textarea
                            id="comentario"
                            value={comentario}
                            onChange={(e) => setComentario(e.target.value)}
                            required
                        ></textarea>
                    </div>
                    <button type="submit">Enviar Avaliação</button>
                </form>

                {/* LISTA DE AVALIAÇÕES EXISTENTES */}
                <div className="lista-avaliacoes">
                    {avaliacoes.length > 0 ? (
                        avaliacoes.map(avaliacao => (
                            <div key={avaliacao.id} className="avaliacao-card">
                                <p><strong>Nota: {avaliacao.nota}/10</strong></p>
                                <p>"{avaliacao.comentario}"</p>
                            </div>
                        ))
                    ) : (
                        <p>Este filme ainda não tem avaliações.</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default FilmeDetalhe;
