import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

// O componente agora recebe uma propriedade 'onLoginSuccess'
function Login({ onLoginSuccess }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        const loginData = { email: email, senha: password };

        try {
            const response = await fetch('http://localhost:8080/usuarios/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginData)
            });

            if (response.ok) {
                onLoginSuccess(); // Avisa o App.js que o login foi bem-sucedido
                navigate('/'); // Navega para a página principal
            } else {
                const errorText = await response.text();
                alert('Falha no login: ' + errorText);
            }
        } catch (error) {
            alert('Não foi possível conectar ao servidor.');
        }
    };

    // O JSX do formulário continua o mesmo...
    return (
        <div className="login-container">
            <form className="login-form" onSubmit={handleSubmit}>
                <h2>Login</h2>
                <div className="input-group">
                    <label htmlFor="email">Email</label>
                    <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <div className="input-group">
                    <label htmlFor="password">Senha</label>
                    <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                </div>
                <button type="submit" className="login-button">Entrar</button>
                <div className="register-link">
                    <p>Não tem uma conta? <Link to="/registro">Registre-se</Link></p>
                </div>
            </form>
        </div>
    );
}

export default Login;
